import React, { useState } from "react";
import logo from "../../components/assets/images/logo_2.png";
import { Link } from "react-router-dom";

const Search = ({ CartItem }) => {
  // State untuk mengontrol apakah tampilan profile ditampilkan atau tidak
  const [showProfile, setShowProfile] = useState(false);

  // Fungsi untuk menampilkan atau menyembunyikan tampilan profile
  const toggleProfile = () => {
    setShowProfile(!showProfile);
  };

  // Fungsi untuk logout
  const handleLogout = () => {
    // Lakukan proses logout di sini, seperti menghapus token atau menghapus sesi pengguna
    console.log("Logout berhasil");
  };

  return (
    <>
      <section className='search'>
        <div className='container c_flex'>
          <div className='logo width'>
            <img src={logo} alt='' />
          </div>

          <div className='search-box f_flex'>
            <i className='fa fa-search'></i>
            <input type='text' placeholder='Search and hit enter...' />
            <span>All Category</span>
          </div>

          <div className='icon f_flex width'>
            {/* Tambahkan event onClick untuk menampilkan atau menyembunyikan tampilan profile */}
            <div className='profile-container'>
              <i className='fa fa-user icon-circle' onClick={toggleProfile}></i>
              {/* Tampilkan atau sembunyikan tampilan profile berdasarkan nilai state */}
              {showProfile && (
                <div className='profile-dropdown'>
                  <div className='profile-menu'>
                    <Link to='/profile'>Profile</Link>
                    <button onClick={handleLogout}>Logout</button>
                  </div>
                </div>
              )}
            </div>
            <div className='cart'>
              <Link to='/cart'>
                <i className='fa fa-shopping-bag icon-circle'></i>
                <span>{CartItem.length === 0 ? "" : CartItem.length}</span>
              </Link>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Search;
